svg.script:
      
	
var id;
var bar;
 var done = 0;
function ProgressBar(info) {
    var stem = {}; // 此函数最后返回的代表进度条的对象。
    var length, outline, barr; // 声明内部变量。
    barr = document.getElementById('done'); // 进度条中绿色的变化部分。
    length = 50400;
    var ll=barr.getAttribute("width");
    function reset() {
        return to(0);
    }
    // 设置进度到某个值。
    function to(value) {
        if (value >= 50400) {
            done = 50400;
            barr.setAttribute('width', length);
        } else {
            done = value;
            barr.setAttribute('width', Math.round(done * length / 50400));
        }
        return stem;
    }
    // 进度变化某个值。
    function advance(step) {
        return to(done + step);
    }
    // 以下给进度条对象添加方法。
    // 获得当前进度值。
    stem.getDone = function() {
        return done
    };
    stem.reset = reset;
    stem.to = to;
    stem.advance = advance;
    return stem; // 返回可供脚本使用的进度条对象。
}
// 测试进度条对象。
function testBar() {
     bar = ProgressBar();
    // 此内部函数每运行一次，增加进度值 1，直到进度值为 100。
    function test() {
        if (bar.getDone() === 33600) {
            clearInterval(id);
        } else {
            bar.advance(100);

            if (bar.getDone() === 0) {

                
                document.getElementById("layer_1").style.display = "block";
                document.getElementById("layer_2").style.display = "none";
                document.getElementById("layer_3").style.display = "none";
                document.getElementById("layer_4").style.display = "none";
            } else if (bar.getDone() === 11200) {

                document.getElementById('layer_1').style.display = "none";
                document.getElementById("layer_2").style.display = "block";
                document.getElementById("layer_3").style.display = "none";
                document.getElementById("layer_4").style.display = "none";
            } else if (bar.getDone() === 22400) {
                document.getElementById('layer_3').style.display = "block";
                document.getElementById("layer_2").style.display = "none";
                document.getElementById("layer_1").style.display = "none";
                document.getElementById("layer_4").style.display = "none";
            } else if (bar.getDone() === 33600) {
                document.getElementById('layer_4').style.display = "block";
                document.getElementById("layer_2").style.display = "none";
                document.getElementById("layer_3").style.display = "none";
                document.getElementById("layer_1").style.display = "none";
            } 
        }
    }
    // 每十分之一秒改变一次进度。
     id = setInterval(test, 100);
}
